INSERT INTO `xx_article` (`id`, `create_date`, `modify_date`, `author`, `content`, `hits`, `is_publication`, `is_top`, `seo_description`, `seo_keywords`, `seo_title`, `title`, `article_category`) VALUES(23, '2013-01-01 12:48:13', '2013-01-01 13:37:52', NULL, '<p class="MsoNormal">\r\n	<span style="font-family:宋体;">亲爱的商城顾客朋友</span><span>:</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span><br />\r\n</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 4</span><span style="font-family:宋体;">月</span><span>20</span><span style="font-family:宋体;">日</span><span>8</span><span style="font-family:宋体;">点</span><span>02</span><span style="font-family:宋体;">分，四川雅安芦山县发生</span><span>7.0</span><span style="font-family:宋体;">级地震，天灾再次降临。</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;"><br />\r\n</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;">&nbsp;&nbsp;&nbsp; 或许您不能亲临灾区现场，但一定和我们一样对同胞的苦难牵肠挂肚。为此，我们在第一时间捐赠了百万元的紧急救灾物资。</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;"><br />\r\n</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;">&nbsp;&nbsp;&nbsp; 四川雅安地震发生以来，全国人民纷纷表达为灾区人民奉献爱心的强烈愿望。因此，我们集合各方力量推出了爱心快通道活动，使得大家可以通过我们的努力为灾区奉献爱心，广大消费者可以结合自身的实 际情况，选择捐赠方式。</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;"><br />\r\n</span>\r\n</p>\r\n<p class="MsoNormal">\r\n	<span style="font-family:宋体;">&nbsp;&nbsp;&nbsp; 您所选择的救灾物资将由我司物流免费运送到雅安爱心站，并接受政府统一调拨，把救灾品切实发到有需要的灾民手中。</span>\r\n</p>', 6, ${bit1}, ${bit1}, NULL, NULL, NULL, '为雅安开辟爱心快通道', 2);
